import * as React from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { createNativeStackNavigator } from '@react-navigation/native-stack';

import { AntDesign, MaterialIcons, Ionicons, FontAwesome5 } from '@expo/vector-icons';

import HomeScreen from '../screens/HomeScreen';
import AddTaskScreen from '../screens/AddTaskScreen';
import SignInScreen from '../screens/SignInScreen';
import SignUpScreen from '../screens/SignUpSreen';
import ProfileScreen from '../screens/ProfileScreen';
import EditProfileScreen from '../screens/EditProfileScreen';


const Tab = createBottomTabNavigator();
const HomeStack = createNativeStackNavigator();
const AddTaskStack = createNativeStackNavigator();
const SignInStack = createNativeStackNavigator();
const SignUpStack = createNativeStackNavigator();
const ProfileStack = createNativeStackNavigator();


const AppNavigator = () => {
    return (
        <NavigationContainer >
            <Tab.Navigator
                screenOptions={{
                    tabBarStyle: {
                        backgroundColor: '#094FAF',
                        borderTopRightRadius: 25,
                        borderTopLeftRadius: 25,
                        height: 70,
                    }
                }}
            // initialRouteName="Home"
            >
                <Tab.Screen
                    name="Home"
                    component={HomeStackScreen}
                    options={{
                        tabBarLabel: '',
                        tabBarIcon: () => <AntDesign name="home" size={35} color="#fff" />,
                        headerShown: false, // Hide the header
                    }}
                />
                <Tab.Screen
                    name="AddTaskTab"
                    component={AddTaskStackScreen}
                    options={{
                        tabBarLabel: '',
                        tabBarIcon: () => <MaterialIcons name="add-box" size={35} color="#fff" />,
                        headerShown: false, // Hide the header 

                    }}
                />

                <Tab.Screen
                    name="ProfileTab"
                    component={ProfileStackScreen}
                    options={{
                        tabBarLabel: '',
                        tabBarIcon: () => <AntDesign name="user" size={35} color="#fff" />,
                        headerShown: false,
                        animation: 'none',
                    }}
                />
                {/* 
                <Tab.Screen
                    name="Sign"
                    component={SignInStackScreen}
                    options={{
                        tabBarLabel: '',
                        tabBarIcon: () => <AntDesign name="user" size={35} color="#fff" />,
                        headerShown: false, // Hide the header for AddTaskScreen

                        tabBarStyle: {
                            display: "none",
                            tabBarVisible: false, // Hide the bottom navigation
                        },

                    }}
                /> */}

            </Tab.Navigator>
        </NavigationContainer>
    );
};
export default AppNavigator;

const HomeStackScreen = () => (
    <HomeStack.Navigator>
        <HomeStack.Screen
            name="HomeStack"
            component={HomeScreen}
            options={{
                title: 'Home Screen',
                animation: 'none',
                headerBackTitleVisible: true,
                headerShown: false,
            }}
        />
        <HomeStack.Screen
            name="AddTask"
            component={AddTaskScreen}
            options={{
                title: 'Create New Task',
                animation: 'none',
                headerTitleAlign: 'center'
            }}
        />
    </HomeStack.Navigator>
);

const AddTaskStackScreen = () => (
    <AddTaskStack.Navigator>
        <AddTaskStack.Screen
            name="AddTaskStack"
            component={AddTaskScreen}
            options={{
                title: 'Create New Task',
                animation: 'none',
                headerTitleAlign: 'center'
            }}
        />
    </AddTaskStack.Navigator>
);
const ProfileStackScreen = () => (
    <ProfileStack.Navigator
        screenOptions={{
            // headerShown: false,
            // headerTitleAlign: 'center'
        }}>

        <ProfileStack.Screen
            name="Profile"
            component={ProfileScreen}
            options={{
                headerShown: false, // comment this to see how the screen look
                headerShadowVisible: false,
                animation: 'none',
                headerStyle: {
                    backgroundColor: '#094FAF'
                },
                headerTitleStyle: {
                    color: '#fff'
                },

            }}
        />

        <ProfileStack.Screen
            name="EditProfileStack"
            component={EditProfileScreen}
            options={{
                title: 'Profile',
                animation: 'none',
                headerBackTitleVisible: false,
            }}
        />

    </ProfileStack.Navigator>
);


const SignInStackScreen = () => (
    <SignInStack.Navigator>
        <SignInStack.Screen
            name="SignIn"
            component={SignInScreen}
            options={{
                title: 'Sign In',
                animation: 'none',
                headerBackTitleVisible: true,
                headerShown: false,

                // tabBarStyle: {
                //     display: "none",
                //     tabBarVisible: false, // Hide the bottom navigation
                // },
            }}
        />

        <SignInStack.Screen
            name="SignUp"
            component={SignUpScreen}
            options={{
                title: 'Sign In',
                animation: 'none',
                headerBackTitleVisible: true,
                headerShown: false,

                // tabBarStyle: {
                //     display: "none",
                //     tabBarVisible: false, // Hide the bottom navigation
                // },
            }}
        />
    </SignInStack.Navigator>
);
