// AddTaskScreen.js

import { View, Text, TextInput, Button, StyleSheet, TouchableOpacity, ScrollView } from 'react-native';
import { RadioButton } from 'react-native-paper';

    function AddTaskScreen(){
    return (
        <ScrollView ScrollView showsVerticalScrollIndicator={false}>
            <View style={styles.container}>

                <TextInput
                     style={styles.input}
                    placeholder="Title"
                />

                <TextInput
                    style={{ ...styles.input, height: 80, }}
                    placeholder="Task Description"
                    multiline
                    numberOfLines={50}
                />

                <TextInput
                    style={styles.input}
                    placeholder="Start Date"
                />

                <TextInput
                    style={styles.input}
                    placeholder="End Date"
                />

                <View style={styles.radioGroup}>
                    <Text>Priority:</Text>
                    <RadioButton.Group >
                        <View style={styles.radioItem}>
                            <RadioButton value="None" />
                            <Text>None</Text>
                        </View>
                        <View style={styles.radioItem}>
                            <RadioButton value="High" />
                            <Text>High</Text>
                        </View>
                    </RadioButton.Group>
                </View>

                {/* <Button
                    title="Create Task"
                    onPress={handleCreateTask}
                    color="#094FAF"

                    // style={styles.button}
                    style={{
                        backgroundColor: 'blue', // Background color
                        borderRadius: 10, // Border radius
                        padding: 10, // Optional: Padding to make it visually appealing
                    }}
                /> */}

                <TouchableOpacity style={styles.addButton}>
                    <Text style={styles.buttonText}>Create Task</Text>
                </TouchableOpacity>

            </View>

        </ScrollView>

    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        padding: 16,
        // backgroundColor: '#fff',
        paddingLeft: 35,
        paddingRight: 35,
        textAlign: 'center',
        paddingTopTop: 50,
    },
    header: {
        fontSize: 24,
        fontWeight: 'bold',
        marginBottom: 16,
    },
    input: {
        height: 40,
        borderColor: '#eee',
        borderWidth: 1,
        marginBottom: 20,
        paddingTop: 12,
        paddingHorizontal: 10,
        backgroundColor: "#eee",
        borderRadius: 5,
        gap: 25,
    },
    radioGroup: {
        flexDirection: 'row',
        alignItems: 'center',
        marginBottom: 12,
    },
    radioItem: {
        flexDirection: 'row',
        alignItems: 'center',
        marginRight: 16,
    },



    addButton: {
        backgroundColor: '#094FAF',
        padding: 13,  // All sides are 10
        paddingHorizontal: 20,  // Left and right are 20
        borderRadius: 15,
        marginTop: 20,
        width: '100%',
        // alignItems: 'center',
        // display: 'flex',
        alignSelf: 'center'
    },
    buttonText: {
        color: 'white',
        // fontWeight: 500,
        textAlign: 'center',
    },


    button: {
        borderRadius: 10, // Set the border radius
        borderWidth: 2, // Set the border width
        borderColor: '#3498db', // Set the border color to blue
        padding: 10, // Add some padding for better visual appearance
    },
});

export default AddTaskScreen;
