import React from "react";
import { View, Text, StyleSheet, TouchableOpacity, ScrollView  } from "react-native";

export default function App (){
  return(
        <View style={styles.container}>
          <View style={styles.header}>
            <View>
              <Text style={styles.headerText}>Hello Barry</Text>
              <Text style={styles.subHeaderText}>Have a nice day!</Text>
            </View>

            <TouchableOpacity style={styles.addButton}>
              <Text style={styles.buttonText}>Add Task</Text>
            </TouchableOpacity>
          </View>

          <ScrollView>
            <View style={styles.taskItem}>
              <Text style={styles.title}>UI Development</Text>
              <Text style={styles.status}> Completed</Text>

              <Text style={styles.description}>I will be creating UI for my CS Acadmy Project</Text>
              <Text style={styles.date}> 1 Nov 2023 - 7 Nov 2023</Text>
            </View>
          </ScrollView>

        </View>
  );
};


const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
    marginTop: 30
  },
  header: {
    flexDirection: 'row',
    justifyContent: 'space-between'
  },
  headerText: {
    fontSize: 16,
    fontWeight: 'bold'
  },
  addButton: {
    backgroundColor: '#094faf',
    padding: 10,
    borderRadius: 15,
    paddingHorizontal: 20
  },
  buttonText: {
    color: 'white',
    fontWeight: 400,
  },
  taskItem: {
    marginTop: 16,
    backgroundColor: "red",

  }
})

